import {useState} from "react";

export default function () {

    const [formData, setFormData] = useState({
        name: '',
        password: '',
    })

    const handleChange = (e) => {
        setFormData(prevState => {
            return {
                ...prevState,
                [e.target.name]: e.target.value,
            }
        })
    }

    const handleSubmit = (e) => {
        e.preventDefault();
    }

    return (
        <form onSubmit={handleSubmit}>
            <label htmlFor={"name"} className={"login-label"}>
                <i className="fa-solid fa-user"></i>
                <input id={"name"} name={"name"} value={formData.name} onClick={handleChange} placeholder={"John Smith"} type={"text"} required={true}/>
            </label>

            <label htmlFor={"password"} className={"login-label"}>
                <i className="fa-solid fa-lock"></i>
                <input id={"password"} name={"password"} value={formData.password} onClick={handleChange} placeholder={"Password"} type={"password"} required={true}/>
            </label>

            <div className={"submit-btn-container"}>
                <input id={"submit-btn"} type={"submit"} value={"Sign In"}/>
            </div>

            <div className={"direct-to-signup"}>
                Don't Have an account?
                <a href={"/signup"}> Sign up</a>
            </div>

        </form>
    )
}
