import "../../css/crash.css"
import {useSelector, useDispatch} from "react-redux";
import {useEffect, useState} from "react";
import state from '../../redux-toolkit'

export default function () {

    const [user, setUser] = useState("");

    const u = useSelector(() => state.getState(user));

    const logout = (e) => {
        e.preventDefault();
    }

    useEffect(() => {
        console.log(state);
        if(u) setUser(u);

    }, [])

    return (
        <header>
            <nav className={"top-nav"}>
                <div className={"nav-logo"} onClick={() => window.location.replace("http://localhost:3000/")}><span className={"company-name"}>BigBull</span></div>
                <div>
                    <ul className={"top-nav-links"}>
                        <li><a href="/tweets" className={"top-nav-link"}>tweets</a></li>
                        <li><a href="/comments" className={"top-nav-link"}>comments</a></li>

                        {user.length?
                            <li><a href="" onClick={logout} className={"top-nav-link"}>logout</a></li>:
                            <li><a href="/login" className={"top-nav-link"}>sign in</a></li>
                        }

                    </ul>
                </div>
                <div className={"modal-popper"}>
                    <i className="fa-solid fa-bars"></i>
                </div>
            </nav>
        </header>
    )

}
